package us.oh.state.epa.stars2.workflow.framework.components;

import java.util.EventObject;

/**
 * <p>
 * Title: SingleListSelectionEvent
 * </p>
 * 
 * <p>
 * Description: This is the event generated by the SingleSelectionList whenever
 * the user has selected one of the entries in the list.
 * </p>
 * 
 * <p>
 * Copyright: Copyright (c) 2004
 * </p>
 * <p>
 * Company: MentorGen, LLC
 * </p>
 * 
 * @author J. E. Collier
 */
public class SingleListSelectionEvent extends EventObject {
    private String selection;
    private int selIdx;

    /**
     * Constructor. The "src" is the SingleSelectionList that generated the
     * event. The "selection" is the value selected by the user. The
     * "selectedIdx" is the index of the user's selection.
     * 
     * @param src
     *            SingleSelectionList that generated the event.
     * @param selection
     *            The value selected by the user.
     * @param selectedIdx
     *            The index of the selected item in the list.
     */
    public SingleListSelectionEvent(Object src, String selection,
            int selectedIdx) {
        super(src);
        this.selection = selection;
        this.selIdx = selectedIdx;
    }

    /**
     * Returns the value selected by the user.
     * 
     * @return String User selection.
     */
    public final String getSelection() {
        return this.selection;
    }

    /**
     * Returns the index of the value selected by the user.
     * 
     * @return int Index of the user selection.
     */
    public final int getSelectedIndex() {
        return this.selIdx;
    }
}
